mapper.select:
select va.*,di.*,ca.*,md.*,di.id as d_id,ca.id as c_id,md.model_definition_id as m_id,ca.phone_number as phone_code from t_vehicle_archives va join t_driver_information di on va.driver_id = di.id
join t_customer_archives ca on va.customer_archives_id = ca.id
join t_model_definition md on va.model_definition_id = md.model_definition_id where ca.customer_name like #{keyWord} or ca.phone_number like #{keyWord} or di.driver_name like #{keyWord} or md.vehicle_type like #{keyWord} or md.range_rover like #{keyWord} or va.vehicle_number like #{keyWord} limit #{page},#{limit}

select count(va.id) from t_vehicle_archives va join t_driver_information di on va.driver_id = di.id
join t_customer_archives ca on va.customer_archives_id = ca.id
join t_model_definition md on va.model_definition_id = md.model_definition_id where ca.customer_name like #{keyWord} or ca.phone_number like #{keyWord} or di.driver_name like #{keyWord} or md.vehicle_type like #{keyWord} or md.range_rover like #{keyWord} or va.vehicle_number like #{keyWord}
